package com.abc.practice;

public class FindFirstAndLastCharacter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String s="GeeksforGeeks";
        int n = s.length();
        System.out.println("First character of a string:"+" "+s.charAt(0));
        System.out.println("Last character of a string:"+" "+s.charAt(n-1));
	}

}
